"use es6";

import React, { useState } from "react";
import stars from "../images/stars.png";
import stripes from "../images/stripes.png";
import FormUI from "../components/FormUI";

const HomePage = () => {
  const [stripesBackgroundImage, setStripesBackgroundImage] = useState();
  const [starsBackgroundImage, setStarsBackgroundImage] = useState();

  const canvasstyle = {
    width: "fit-content",
    height: "fit-content",
  };

  const stripesstyle = {
    backgroundColor: "#770000",
    backgroundImage: `url(${stripesBackgroundImage})`,
    width: "fit-content",
    height: "fit-content",
    position: "absolute",
    top: "350px",
    left: "500px",
  };

  const borderstyle = {
    backgroundColor: "#ffffff",
    width: "fit-content",
    height: "fit-content",
    position: "absolute",
    top: "350px",
    left: "500px",
  };

  const starstyle = {
    backgroundColor: "#000077",
    backgroundImage: `url(${starsBackgroundImage})`,
    width: "fit-content",
    height: "fit-content",
    position: "absolute",
    top: "325px",
    left: "475px",
  };

  const updateBackground = (link, stripes) => {
    const Http = new XMLHttpRequest();
    const url = `https://image-grabber-api.herokuapp.com/url/${link}`;
    Http.open("GET", url);
    Http.send();

    if (stripes) {
      Http.onreadystatechange = (e) => {
        setStripesBackgroundImage(
          Http.responseText.split('"')[1].split("?")[0]
        );
      };
    } else {
      Http.onreadystatechange = (e) => {
        setStarsBackgroundImage(Http.responseText.split('"')[1].split("?")[0]);
      };
    }
  };

  return (
    <>
      <div>
        <h1>HOME</h1>
        <h3>Update stars background:</h3>
        <FormUI
          submissionAction={(e) => {
            updateBackground(e, false);
          }}
        />

        <h3>Update stripes background:</h3>
        <FormUI
          submissionAction={(e) => {
            updateBackground(e, false);
          }}
        />
      </div>

      <div style={{ margin: "auto" }}>
        <div style={stripesstyle}>
          <img src={stripes} alt={"stripes"} width={"50%"} />
        </div>
        <div style={borderstyle}>
          <img src={stars} alt={"border"} width={"50%"} />
        </div>
        <div style={starstyle}>
          <img src={stars} alt={"stars"} width={"50%"} />
        </div>
      </div>
    </>
  );
};

export default HomePage;
