"use es6";

import React, { useState } from "react";
import stars from "../images/stars.png";
import stripes from "../images/stripes.png";
import * as d3 from "d3";

const HomePage = () => {
  const [stripesBackgroundImage, setStripesBackgroundImage] = useState();
  const [starsBackgroundImage, setStarsBackgroundImage] = useState();

  const canvasstyle = {
    width: "fit-content",
    height: "fit-content",
  };

  const stripesstyle = {
    backgroundColor: "#770000",
    backgroundImage: `url(${stripesBackgroundImage})`,
    width: "fit-content",
    height: "fit-content",
    position: "absolute",
    top: "150px",
    left: "500px",
  };

  const borderstyle = {
    backgroundColor: "#ffffff",
    width: "fit-content",
    height: "fit-content",
    position: "absolute",
    top: "150px",
    left: "500px",
  };

  const starstyle = {
    backgroundColor: "#000077",
    backgroundImage: `url(${starsBackgroundImage})`,
    width: "fit-content",
    height: "fit-content",
    position: "absolute",
    top: "125px",
    left: "475px",
  };

  // const getImgLink = (link) => {
  //   console.log(link);
  //   fetch(`https://image-grabber-api.herokuapp.com/url/${link}`, {
  //     method: "GET",
  //     headers: {
  //       "Content-Type": "application/json",
  //     },
  //   });
  //   console.log(link);
  // };

  const updatebg = (link) => {
    const Http = new XMLHttpRequest();
    const url = `https://image-grabber-api.herokuapp.com/url/${link}`;
    Http.open("GET", url);
    Http.send();

    Http.onreadystatechange = (e) => {
      console.log(Http.responseText);
      setStripesBackgroundImage(Http.responseText);
    };
  };

  return (
    <>
      <div>
        <h1>HOME</h1>
        <div style={stripesstyle}>
          <img src={stripes} alt={"stripes"} />
        </div>
        <div style={borderstyle}>
          <img src={stars} alt={"border"} />
        </div>
        <div style={starstyle}>
          <img src={stars} alt={"stars"} />
        </div>
      </div>
      <button
        onClick={() => {
          updatebg("www.disney.com");
        }}
      >
        UPDATE
      </button>
    </>
  );
};

export default HomePage;
